mapper.update:
UPDATE CHAT_MESSAGE
SET READ_YN = 'Y'
WHERE CHAT_RELATION_IDX = #{relationIdx}
  AND RECEIVER_ID = #{userIdx}

mapper.select:
SELECT RELATION_IDX
FROM RELATION
WHERE (USER1_ID = #{userId}
    OR USER2_ID = #{userId})
  AND (USER1_ID = #{registerId}
    OR USER2_ID = #{registerId})

SELECT RELATION_IDX AS value
FROM RELATION
WHERE (USER1_ID = #{userId}
   OR USER2_ID = #{userId})
  AND (USER1_ID = #{registerId}
   OR USER2_ID = #{registerId})
