mapper.select:
SELECT PHOTOURL, POST_IDX AS POSTIDX, REGISTER_ID as registerId, PRICE, TITLE, CONTENT, TRANSACTION_CONFIRMATION AS transactionConfirmation
 FROM POST
 WHERE POST_IDX = #{postIdx}

SELECT PHOTOURL, POST_IDX AS POSTIDX, REGISTER_ID as registerId, PRICE, TITLE, CONTENT, TRANSACTION_CONFIRMATION
FROM POST
WHERE POST_IDX = #{postIdx}

mapper.update:
UPDATE POST
SET TRANSACTION_CONFIRMATION = 'WAIT'
WHERE POST_IDX = #{postIdx}

UPDATE POST
SET TRANSACTION_CONFIRMATION = 'no'
WHERE POST_IDX = #{postIdx}
